import React from 'react';
import { Form, FormInput, FormGroup, Button, Card, CardBody, CardTitle, Progress } from "shards-react";
import "./MovieDetailsPage.css";
import MovieDetailsCard from '../components/MovieDetailsCard';


import {
    Table,
    Pagination,
    Select,
    Row,
    Col,
    Divider,

} from 'antd'
import { RadarChart } from 'react-vis';
import { format } from 'd3-format';

import { getMovie} from '../fetcher'


import MenuBar from '../components/MenuBar';

const { Column, ColumnGroup } = Table;
const movieDetail1 = [{"Title":"Inception","Year":2010,"Language":"en","Overview":"Cobb, a skilled thief who commits corporate espionage by infiltrating the subconscious of his targets is offered a chance to regain his old life as payment for a task considered to be impossible: \"inception\", the implantation of another person's idea into a target's subconscious.",
"Popularity":29.1081,"Runtime":148,"Type":"movie","Genres":"Action, Adventure, Mystery, Sci-Fi, ScienceFiction, Thriller","Companies":"Legendary Pictures, Syncopy, Warner Bros.",
"Countries":"United Kingdom, United States of America","SpokenLanguages":"English",
"PosterLink":"https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_.jpg","URL":"https://www.imdb.com/title/tt1375666/","RatingValue":8.8,"RatingCount":2050656}];


class MovieDetailsPage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            titleQuery: "",
            yearQuery: "",
            movieResults: [],
            selectedMovieId: window.location.search ? window.location.search.substring(1).split('=')[1] : 0,
            selectedMovieDetails: null,
            movieDetail: []

        }

        this.handleTitleQueryChange = this.handleTitleQueryChange.bind(this)
        this.handleYearQueryChange = this.handleYearQueryChange.bind(this)
        this.updateSearchResults = this.updateSearchResults.bind(this)
        this.goToMatch = this.goToMatch.bind(this)
    }



    handleTitleQueryChange(event) {
        this.setState({ titleQuery: event.target.value })
    }

    handleYearQueryChange(event) {
        this.setState({ yearQuery: event.target.value })
    }

    goToMatch(matchId) {
        window.location = `/matches?id=${matchId}`
    }

    updateSearchResults() {
        //TASK 11: call getMatchSearch and update matchesResults in state. See componentDidMount() for a hint
        getMovie(this.state.titleQuery, this.state.yearQuery).then(res => {
            this.setState({ matchesResults: res.results })
        })
    }

    componentDidMount() {
        getMovie(this.state.titleQuery, this.state.yearQuery).then(res => {
            this.setState({movieDetail: res.results})
        })
        
        // getMovieSearch(this.state.selectedMovieId).then(res => {
        //     this.setState({ selectedMovieDetails: res.results[0] })
        // })
    }

    render() {
        return (
            <div>
                <MenuBar />
                {/* <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
                <div style={{ width: '70vw', margin: '0 auto', marginTop: '5vh' }}>
                    <h1>{this.state.movieDetail.Title}</h1>
                    <h1>Test heading</h1>
                    </div>
                </Form> */}
                <Divider />

                <Card>
                        <CardBody>
                        <Row gutter='30' align='middle' justify='center'>
                            <Col flex={2} style={{ textAlign: 'left' }}>
                            <h1>{this.state.movieDetail.Title}</h1>
                            </Col>

                            <Col flex={2} style={{ textAlign: 'right' }}>
                            <img src={movieDetail1.PosterLink !== "N/A" ? movieDetail1.PosterLink : "https://via.placeholder.com/400"} alt={Title} />
                            </Col>
                        </Row>
                            <Row gutter='30' align='middle' justify='left'>
                            {/* Title, Year, Language, Overview, Popularity, Runtime, Type, Genres, Companies, Countries, SpokenLanguages, sPosterLink, URL, RatingValue, RatingCount  */}
                                <Col>
                                <h5>{this.state.movieDetail.Year}</h5>
                                </Col>
                                <Col>
                                <h5>{this.state.movieDetail.Language}</h5>
                                </Col>
                                <Col>
                                <h5>{this.state.movieDetail.Overview}</h5>
                                </Col>
                            </Row>
                            <br>
                            </br>
                            <Row gutter='30' align='middle' justify='left'>
                                <Col>
                                Runtime: {this.state.movieDetail.Runtime}
                                </Col>
                                {/* TASK 28: add two more columns here for Height and Weight, with the appropriate labels as above */}
                                <Col>
                                Type: {}
                                </Col>
                                <Col>
                                Weight: {}
                                </Col>

                            </Row>
                            <Row gutter='30' align='middle' justify='left'>
                                <Col>
                                Value: {}
                                </Col>
                                <Col>
                                Release Clause: {}
                                </Col>
                                {/* TASK 29: Create 2 additional columns for the attributes 'Wage' and 'Contract Valid Until' (use spaces between the words when labelling!) */}
                                <Col>
                                Wage: {}
                                </Col>
                                <Col>
                                Contract Valid Until: {}
                                </Col>
                            </Row>
                        </CardBody>

                    </Card>


                {/* {movie1?.length > 0 ? (
                                <div className="container">
                                    {movie1.map((movie) => (
                                        <MovieCard movie={movie} />
                                ))}
                            </div>
                            ) : (
                            <div className="empty">
                                <h2>No movies found</h2>
                            </div>
                            )} */}


                            {this.state.movieDetail?.length > 0 ? (
                                <div className="container">
                                    {this.state.movieDetail.map((movie) => (
                                        <MovieDetailsCard movie={movie} />
                                ))}
                            </div>
                            ) : (
                            <div className="empty">
                                <h2>Movie not found</h2>
                            </div>
                            )}

 

            </div>
        )
    }
}

export default MatchesPage
